package com.tcs.vendor.bean;

import javax.xml.bind.annotation.XmlRootElement;


@XmlRootElement
public class Item {
	private int i_id;
	private String v_id;
	private String i_name;
	private double i_price;
	private String i_type;
	private int i_mo;
	private int i_tu;
	private int i_we;
	private int i_th;
	private int i_fr;
	private int i_sa;
	private int i_su;
	private int i_status;
	private int i_update;
	private String comment;
	private int serve_count;
	
	public Item(int i_id, String v_id, String i_name, double i_price,
			String i_type, int i_mo, int i_tu, int i_we, int i_th, int i_fr,
			int i_sa, int i_su, int i_status, int i_update, String comment,
			int serve_count) {
		super();
		this.i_id = i_id;
		this.v_id = v_id;
		this.i_name = i_name;
		this.i_price = i_price;
		this.i_type = i_type;
		this.i_mo = i_mo;
		this.i_tu = i_tu;
		this.i_we = i_we;
		this.i_th = i_th;
		this.i_fr = i_fr;
		this.i_sa = i_sa;
		this.i_su = i_su;
		this.i_status = i_status;
		this.i_update = i_update;
		this.comment = comment;
		this.serve_count = serve_count;
	}

	public String getComment() {
		return comment;
	}

	public void setComment(String comment) {
		this.comment = comment;
	}

	public int getServe_count() {
		return serve_count;
	}

	public void setServe_count(int serve_count) {
		this.serve_count = serve_count;
	}

	public Item() {
		super();
	}
	public int getI_id() {
		return i_id;
	}
	public void setI_id(int i_id) {
		this.i_id = i_id;
	}
	public String getV_id() {
		return v_id;
	}
	public void setV_id(String v_id) {
		this.v_id = v_id;
	}
	public String getI_name() {
		return i_name;
	}
	public void setI_name(String i_name) {
		this.i_name = i_name;
	}
	public double getI_price() {
		return i_price;
	}
	public void setI_price(double i_price) {
		this.i_price = i_price;
	}
	public String getI_type() {
		return i_type;
	}
	public void setI_type(String i_type) {
		this.i_type = i_type;
	}
	public int getI_mo() {
		return i_mo;
	}
	public void setI_mo(int i_mo) {
		this.i_mo = i_mo;
	}
	public int getI_tu() {
		return i_tu;
	}
	public void setI_tu(int i_tu) {
		this.i_tu = i_tu;
	}
	public int getI_we() {
		return i_we;
	}
	public void setI_we(int i_we) {
		this.i_we = i_we;
	}
	public int getI_th() {
		return i_th;
	}
	public void setI_th(int i_th) {
		this.i_th = i_th;
	}
	public int getI_fr() {
		return i_fr;
	}
	public void setI_fr(int i_fr) {
		this.i_fr = i_fr;
	}
	public int getI_sa() {
		return i_sa;
	}
	public void setI_sa(int i_sa) {
		this.i_sa = i_sa;
	}
	public int getI_su() {
		return i_su;
	}
	public void setI_su(int i_su) {
		this.i_su = i_su;
	}
	public int getI_status() {
		return i_status;
	}
	public void setI_status(int i_status) {
		this.i_status = i_status;
	}
	public int getI_update() {
		return i_update;
	}
	public void setI_update(int i_update) {
		this.i_update = i_update;
	}
	
	
	
}
